mapper.select:

where 1=1

    and id = #{param.id}


    and organization_id = #{param.organizationId}


    and note_name = #{param.noteName}


    and note_tags = #{param.noteTags}


    and description = #{param.description}


    and broad_category = #{param.broadCategory}


    and narrow_category = #{param.narrowCategory}


    and note_grade = #{param.noteGrade}


    and note_mode = #{param.noteMode}


    and suitable_users = #{param.suitableUsers}


    and pic = #{param.pic}


    and audit_status = #{param.auditStatus}


    and `status` = #{param.status}


    and deleted = #{param.deleted}
