mapper.select:

where 1=1

    and id = #{param.id}


    and tenant_id = #{param.tenantId}


    and tenant_code = #{param.tenantCode}


    and tenant_name = #{param.tenantName}


    and tenant_type = #{param.tenantType}


    and expire_time = #{param.expireTime}


    and parent_tenant_id = #{param.parentTenantId}


    and tenant_contact = #{param.tenantContact}


    and tenant_phone = #{param.tenantPhone}


    and tenant_email = #{param.tenantEmail}


    and isolation_level = #{param.isolationLevel}


    and max_user_num = #{param.maxUserNum}


    and audit_status = #{param.auditStatus}


    and audit_by = #{param.auditBy}


    and audit_time = #{param.auditTime}


    and create_by = #{param.createBy}


    and create_name = #{param.createName}


    and create_time = #{param.createTime}


    and update_by = #{param.updateBy}


    and update_name = #{param.updateName}


    and update_time = #{param.updateTime}


    and deleted = #{param.deleted}
